pub enum Event {

    /// A single key event with additional pressed modifiers.
    Key(KeyEvent),

    /// A single mouse event with additional pressed modifiers.
    Mouse(MouseEvent),

    /// An resize event with new dimensions after resize (columns, rows).
    Resize(u16, u16),
}

pub enum MouseEvent {

    /// Pressed mouse button.
    ///
    /// Contains mouse button, pressed pointer location (column, row), and additional key modifiers.
    Down(MouseButton, u16, u16, KeyModifiers),

    /// Released mouse button.
    ///
    /// Contains mouse button, released pointer location (column, row), and additional key modifiers.
    Up(MouseButton, u16, u16, KeyModifiers),

    /// Moved mouse pointer while pressing a mouse button.
    ///
    /// Contains the pressed mouse button, released pointer location (column, row), and additional key modifiers.
    Drag(MouseButton, u16, u16, KeyModifiers),

    /// Scrolled mouse wheel downwards (towards the user).
    ///
    /// Contains the scroll location (column, row), and additional key modifiers.
    ScrollDown(u16, u16, KeyModifiers),

    /// Scrolled mouse wheel upwards (away from the user).
    ///
    /// Contains the scroll location (column, row), and additional key modifiers.
    ScrollUp(u16, u16, KeyModifiers),
}

pub enum MouseButton {

    /// Left mouse button.
    Left,

    /// Right mouse button.
    Right,

    /// Middle mouse button.
    Middle,
}

pub enum KeyCode {

    /// Backspace key.
    Backspace,

    /// Enter key.
    Enter,

    /// Left arrow key.
    Left,

    /// Right arrow key.
    Right,

    /// Up arrow key.
    Up,

    /// Down arrow key.
    Down,

    /// Home key.
    Home,

    /// End key.
    End,

    /// Page up key.
    PageUp,

    /// Page dow key.
    PageDown,

    /// Tab key.
    Tab,

    /// Shift + Tab key.
    BackTab,

    /// Delete key.
    Delete,

    /// Insert key.
    Insert,

    /// F key.
    ///
    /// `KeyEvent::F(1)` represents F1 key, etc.
    F(u8),

    /// A character.
    ///
    /// `KeyEvent::Char('c')` represents `c` character, etc.
    Char(char),

    /// Null.
    Null,

    /// Escape key.
    Esc,
}

pub enum Color {

    /// Resets the terminal color.
    Reset,

    /// Black color.
    Black,

    /// Dark grey color.
    DarkGrey,

    /// Light red color.
    Red,

    /// Dark red color.
    DarkRed,

    /// Light green color.
    Green,

    /// Dark green color.
    DarkGreen,

    /// Light yellow color.
    Yellow,

    /// Dark yellow color.
    DarkYellow,

    /// Light blue color.
    Blue,

    /// Dark blue color.
    DarkBlue,

    /// Light magenta color.
    Magenta,

    /// Dark magenta color.
    DarkMagenta,

    /// Light cyan color.
    Cyan,

    /// Dark cyan color.
    DarkCyan,

    /// White color.
    White,

    /// Grey color.
    Grey,

    /// An RGB color. See [RGB color model](https://en.wikipedia.org/wiki/RGB_color_model) for more info.
    ///
    /// Most UNIX terminals and Windows 10 supported only.
    /// See [Platform-specific notes](enum.Color.html#platform-specific-notes) for more info.
    Rgb {
        r: u8,
        g: u8,
        b: u8,
    },

    /// An ANSI color. See [256 colors - cheat sheet](https://jonasjacek.github.io/colors/) for more info.
    ///
    /// Most UNIX terminals and Windows 10 supported only.
    /// See [Platform-specific notes](enum.Color.html#platform-specific-notes) for more info.
    AnsiValue(u8),
}

pub enum Colored {

    /// A foreground color.
    ForegroundColor(Color),

    /// A background color.
    BackgroundColor(Color),
}

pub enum ClearType {

    /// All cells.
    All,

    /// All cells from the cursor position downwards.
    FromCursorDown,

    /// All cells from the cursor position upwards.
    FromCursorUp,

    /// All cells at the cursor row.
    CurrentLine,

    /// All cells from the cursor position until the new line.
    UntilNewLine,
}
