pub enum EventResult {

    /// The event was ignored. The parent can keep handling it.
    Ignored,

    /// The event was consumed. An optionnal callback to run is attached.
    Consumed(Option<Callback>),
}

pub enum Key {

    /// Both Enter (or Return) and numpad Enter
    Enter,

    /// Tabulation key
    Tab,

    /// Backspace key
    Backspace,

    /// Escape key
    Esc,

    /// Left arrow
    Left,

    /// Right arrow
    Right,

    /// Up arrow
    Up,

    /// Down arrow
    Down,

    /// Insert key
    Ins,

    /// Delete key
    Del,

    /// Home key
    Home,

    /// End key
    End,

    /// Page Up key
    PageUp,

    /// Page Down key
    PageDown,

    /// Pause Break key
    PauseBreak,

    /// The 5 in the center of the keypad, when numlock is disabled.
    NumpadCenter,

    /// F0 key
    F0,

    /// F1 key
    F1,

    /// F2 key
    F2,

    /// F3 key
    F3,

    /// F4 key
    F4,

    /// F5 key
    F5,

    /// F6 key
    F6,

    /// F7 key
    F7,

    /// F8 key
    F8,

    /// F9 key
    F9,

    /// F10 key
    F10,

    /// F11 key
    F11,

    /// F12 key
    F12,
}

pub enum MouseButton {

    /// The left button, used for main actions.
    Left,

    /// Middle button, probably the wheel. Often pastes text in X11 on linux.
    Middle,

    /// The right button, for special actions.
    Right,

    /// Fourth button if the mouse supports it.
    Button4,

    /// Fifth button if the mouse supports it.
    Button5,

    #[doc(hidden)]
    Other,
}

pub enum MouseEvent {

    /// A button was pressed.
    Press(MouseButton),

    /// A button was released.
    Release(MouseButton),

    /// A button is being held.
    Hold(MouseButton),

    /// The wheel was moved up.
    WheelUp,

    /// The wheel was moved down.
    WheelDown,
}

pub enum Event {

    /// Event fired when the window is resized.
    WindowResize,

    /// Event fired regularly when a auto-refresh is set.
    Refresh,

    /// A character was entered (includes numbers, punctuation, ...).
    Char(char),

    /// A character was entered with the Ctrl key pressed.
    CtrlChar(char),

    /// A character was entered with the Alt key pressed.
    AltChar(char),

    /// A non-character key was pressed.
    Key(Key),

    /// A non-character key was pressed with the Shift key pressed.
    Shift(Key),

    /// A non-character key was pressed with the Alt key pressed.
    Alt(Key),

    /// A non-character key was pressed with the Shift and Alt keys pressed.
    AltShift(Key),

    /// A non-character key was pressed with the Ctrl key pressed.
    Ctrl(Key),

    /// A non-character key was pressed with the Ctrl and Shift keys pressed.
    CtrlShift(Key),

    /// A non-character key was pressed with the Ctrl and Alt keys pressed.
    CtrlAlt(Key),

    /// A mouse event was sent.
    Mouse {
        /// Position of the top-left corner of the view receiving this event.
        offset: Vec2,
        /// Position of the mouse when this event was fired.
        position: Vec2,
        /// The mouse event itself.
        event: MouseEvent,
    },

    /// An unknown event was received.
    Unknown(Vec<u8>),

    #[doc(hidden)]
    /// The application is about to exit.
    Exit,
}

pub enum Selector<'a> {

    /// Selects a view from its ID.
    Id(&'a str),

    /// Selects a view from its path.
    Path(&'a ViewPath),
}

pub enum Offset {

    /// In the center of the screen
    Center,

    /// Place top-left corner at the given absolute coordinates
    Absolute(usize),

    /// Offset from the previous layer's top-left corner.
    ///
    /// If this is the first layer, behaves like `Absolute`.
    Parent(isize),
}

pub enum SizeConstraint {

    /// No constraint imposed, the child view's response is used.
    Free,

    /// Tries to take all available space, no matter what the child needs.
    Full,

    /// Always return the included size, no matter what the child needs.
    Fixed(usize),

    /// Returns the minimum of the included value and the child view's size.
    AtMost(usize),

    /// Returns the maximum of the included value and the child view's size.
    AtLeast(usize),
}

pub enum ScrollStrategy {

    /// Keeps the same row number
    KeepRow,

    /// Sticks to the top.
    StickToTop,

    /// Sticks to the bottom of the view.
    StickToBottom,
}

pub enum IndexedCow {

    /// Indexes content in a separate string.
    Borrowed {
        /// Byte offset of the beginning of the span (inclusive)
        start: usize,
        /// Byte offset of the end of the span (exclusive)
        end: usize,
    },

    /// Owns its content.
    Owned(String),
}

pub enum HAlign {

    /// Place the element to the left of available space
    Left,

    /// Place the element horizontally in the center of available space
    Center,

    /// Place the element to the right of available space
    Right,
}

pub enum VAlign {

    /// Place the element at the top of available space
    Top,

    /// Place the element vertically in the center of available space
    Center,

    /// Place the element at the bottom of available space
    Bottom,
}

pub enum Orientation {

    /// Horizontal orientation
    Horizontal,

    /// Vertical orientation
    Vertical,
}

pub enum Direction {

    /// An absolute direction.
    Abs(Absolute),

    /// A direction relative to the current orientation.
    Rel(Relative),
}

pub enum Relative {

    /// Front relative direction.
    ///
    /// * Horizontally, this means `Left`
    /// * Vertically, this means `Up`
    Front,

    /// Back relative direction.
    ///
    /// * Horizontally, this means `Right`
    /// * Vertically, this means `Down`.
    Back,
}

pub enum Absolute {

    /// Left
    Left,

    /// Up
    Up,

    /// Right
    Right,

    /// Down
    Down,

    /// No real direction.
    ///
    /// Used when the "direction" is accross layers for instance.
    None,
}

pub enum MenuItem {

    /// Actionnable button with a label.
    Leaf(String, Callback),

    /// Sub-menu with a label.
    Subtree(String, Rc<MenuTree>),

    /// Delimiter without a label.
    Delimiter,
}

pub enum BorderStyle {

    /// Simple borders.
    Simple,

    /// Outset borders with a simple 3d effect.
    Outset,

    /// No borders.
    None,
}

pub enum BaseColor {

    /// Black color
    ///
    /// Color #0
    Black,

    /// Red color
    ///
    /// Color #1
    Red,

    /// Green color
    ///
    /// Color #2
    Green,

    /// Yellow color (Red + Green)
    ///
    /// Color #3
    Yellow,

    /// Blue color
    ///
    /// Color #4
    Blue,

    /// Magenta color (Red + Blue)
    ///
    /// Color #5
    Magenta,

    /// Cyan color (Green + Blue)
    ///
    /// Color #6
    Cyan,

    /// White color (Red + Green + Blue)
    ///
    /// Color #7
    White,
}

pub enum Color {

    /// Represents a color, preset by terminal.
    TerminalDefault,

    /// One of the 8 base colors.
    ///
    /// Note: the actual color used depends on the terminal configuration.
    Dark(BaseColor),

    /// Lighter version of a base color.
    ///
    /// Note: the actual color used depends on the terminal configuration.
    Light(BaseColor),

    /// True-color, 24-bit.
    Rgb(u8, u8, u8),

    /// Low-resolution
    ///
    /// Each value should be `<= 5` (you'll get panics otherwise).
    ///
    /// These 216 possible colors are part of the default color palette.
    RgbLowRes(u8, u8, u8),
}

pub enum ColorType {

    /// Uses a color from the application palette.
    Palette(PaletteColor),

    /// Uses a direct color, independent of the current palette.
    Color(Color),
}

pub enum Effect {

    /// No effect
    Simple,

    /// Reverses foreground and background colors
    Reverse,

    /// Prints foreground in bold
    Bold,

    /// Prints foreground in italic
    Italic,

    /// Prints foreground with underline
    Underline,
}

pub enum PaletteNode {

    /// A single color.
    Color(Color),

    /// A group of values bundled in the same namespace.
    ///
    /// Namespaces can be merged in the palette with `Palette::merge`.
    Namespace(HashMap<String, PaletteNode>),
}

pub enum PaletteColor {

    /// Color used for the application background.
    Background,

    /// Color used for View shadows.
    Shadow,

    /// Color used for View backgrounds.
    View,

    /// Primary color used for the text.
    Primary,

    /// Secondary color used for the text.
    Secondary,

    /// Tertiary color used for the text.
    Tertiary,

    /// Primary color used for title text.
    TitlePrimary,

    /// Secondary color used for title text.
    TitleSecondary,

    /// Color used for highlighting text.
    Highlight,

    /// Color used for highlighting inactive text.
    HighlightInactive,
}

pub enum Error {

    /// An error occured when reading the file.
    Io(io::Error),

    /// An error occured when parsing the toml content.
    Parse(toml::de::Error),
}

pub enum DialogFocus {

    /// Content element focused
    Content,

    /// One of buttons focused
    Button(usize),
}

pub enum ListChild {

    /// A single row, with a label and a view.
    Row(String, Box<View>),

    /// A delimiter between groups.
    Delimiter,
}

pub enum LayerPosition {

    /// Starts from the back (bottom) of the stack.
    FromBack(usize),

    /// Starts from the front (top) of the stack.
    FromFront(usize),
}
